
public class D5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=6;
		for (int i = 1; i < n; i++) {
			for (int j = 1; j < n; j++) {
				if (i+j==n)
					System.out.print("1 ");
				else {
					System.out.print("0 ");
				}
			}
			System.out.println();
	}

}
}